import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { TaskService } from '../services/task.service';
import Swal from 'sweetalert2';
@Component({
  selector: 'app-task',
  templateUrl: './task.component.html',
  styleUrls: ['./task.component.scss']
})
export class TaskComponent implements OnInit {

  tasks: any[] = [];  // Tasks for the logged-in user (My Tasks)
  allTasks: any[] = [];  // All tasks
  taskForm: FormGroup;  // Form group for adding a task

  constructor(
    private taskService: TaskService,
    private fb: FormBuilder
  ) {
    this.taskForm = this.fb.group({
      description: ['', [Validators.required]],
      emailto: ['', [Validators.required, Validators.email]]
    });
  }

  ngOnInit(): void {
    this.getAllTasks();
    this.getMyTasks(); 

  }

  // Fetch tasks assigned to the logged-in user (My Tasks)
  getMyTasks(): void {
    this.taskService.getMyTasks().subscribe(
      (res) => {
        this.tasks = res;
      },
      (error) => {
        console.error('Error fetching tasks:', error);
      }
    );
  }

  // Fetch all tasks (All Tasks)
  getAllTasks(): void {
    this.taskService.getAllTasks().subscribe(
      (res) => {
        this.allTasks = res;
      },
      (error) => {
        console.error('Error fetching all tasks:', error);
      }
    );
  }

  // Create a new task
  createTask(): void {
    if (this.taskForm.invalid) {
      Swal.fire('Validation Error', 'Please fill in all required fields.', 'error');
      return;
    }

    const { description, emailto } = this.taskForm.value;

    this.taskService.createTask(description, emailto).subscribe(
      (res) => {
        Swal.fire('Success', 'Task created successfully!', 'success');
        this.getMyTasks();  // Refresh tasks after task is created
        this.taskForm.reset();  // Reset the form after task creation
      },
      (error) => {
        Swal.fire('Error', 'Failed to create task. Please try again.', 'error');
        console.error('Error creating task:', error);
      }
    );
  }

  // Finish the task and update the UI
  onFinishTask(taskId: number): void {
    this.taskService.finishTask(taskId).subscribe(
      (response) => {
        console.log('Task finished:', response);
        // Update the task status locally to reflect the change in the UI
        const updatedTask = this.tasks.find(task => task.id === taskId);
        if (updatedTask) {
          updatedTask.completed = true;
        }
      },
      (error) => {
        console.error('Error finishing task:', error);
      }
    );
  }

  // Handle tab change
  onTabChange(event: any): void {
    if (event.index === 0) {
      this.getMyTasks();
    } else if (event.index === 1) {
      this.getAllTasks();
    }
  }
}

<section id="tasks" class="tasks-section" style="margin-top: 100px;" >
  <div class="task-container">
    <!-- Material Tabs for navigation -->
    <mat-tab-group (selectedIndexChange)="onTabChange($event)">
      <!-- Create Task Tab -->
      <mat-tab label="Create Task">
        <div class="task-form-container">
          <form [formGroup]="taskForm" (ngSubmit)="createTask()" class="task-form">
            <mat-form-field appearance="fill" class="form-field">
              <mat-label>Description</mat-label>
              <input matInput formControlName="description" placeholder="Enter task description" />
              <mat-error *ngIf="taskForm.get('description')?.invalid && taskForm.get('description')?.touched">
                Description is required.
              </mat-error>
            </mat-form-field>

            <mat-form-field appearance="fill" class="form-field">
              <mat-label>Assign To (Email)</mat-label>
              <input matInput formControlName="emailto" placeholder="Enter email" />
              <mat-error *ngIf="taskForm.get('emailto')?.invalid && taskForm.get('emailto')?.touched">
                Valid email is required.
              </mat-error>
            </mat-form-field>

            <button mat-raised-button color="primary" type="submit" class="submit-button">Create Task</button>
          </form>
        </div>
      </mat-tab>

      <!-- My Tasks Tab -->
      <mat-tab label="My Tasks">
        <div class="task-list">
          <div *ngFor="let task of tasks" class="task-item">
            <div class="task-details">
              <p><strong>Description:</strong> {{ task.description }}</p>
              <p><strong>Assigned To:</strong> {{ task.assignedTo.email }}</p>
              <p><strong>Status:</strong>
                <span [ngClass]="{ 'completed': task.completed, 'pending': !task.completed }">
                  <mat-icon *ngIf="task.completed">check_circle</mat-icon>
                  <mat-icon *ngIf="!task.completed">radio_button_unchecked</mat-icon>
                  {{ task.completed ? 'Completed' : 'Pending' }}
                </span>
              </p>
            </div>
            <button mat-raised-button color="accent" (click)="onFinishTask(task.id)" *ngIf="!task.completed" class="finish-button">
              <mat-icon>check_circle</mat-icon> Complete Task
            </button>
          </div>
        </div>
      </mat-tab>

      <!-- All Tasks Tab -->
      <mat-tab label="All Tasks">
        <div class="task-list">
          <div *ngFor="let task of allTasks" class="task-item">
            <div class="task-details">
              <p><strong>Description:</strong> {{ task.description }}</p>
              <p><strong>Assigned To:</strong> {{ task.assignedTo.email }}</p>
              <p><strong>Status:</strong>
                <span [ngClass]="{ 'completed': task.completed, 'pending': !task.completed }">
                  <mat-icon *ngIf="task.completed">check_circle</mat-icon>
                  <mat-icon *ngIf="!task.completed">radio_button_unchecked</mat-icon>
                  {{ task.completed ? 'Completed' : 'Pending' }}
                </span>
              </p>
            </div>
            <button mat-raised-button color="accent" (click)="onFinishTask(task.id)" *ngIf="!task.completed" class="finish-button">
              <mat-icon>check_circle</mat-icon> Complete Task
            </button>
          </div>
        </div>
      </mat-tab>
    </mat-tab-group>
  </div>
</section>
