import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { environment } from 'src/environments/environment';
import { Observable } from 'rxjs/internal/Observable';

@Injectable({
    providedIn: 'root'
})
export class HousingService {
    url = environment.apiURL;
    constructor(private http: HttpClient) { }

    private getAuthHeaders(): HttpHeaders {
        const token = localStorage.getItem('token');
        return new HttpHeaders({
          'Content-Type': 'application/json',
          'Authorization': token ? `Bearer ${token}` : ''
        });
      }

    getAllHousing(): Observable<any[]> {
        return this.http.get<any[]>(`${this.url}/housing`,{
            headers: this.getAuthHeaders()
          }); // Assuming your endpoint is /housing/all
       
    }
    countHousing(): Observable<String> {
        return this.http.get<String>(this.Url+"/count", {
          headers: this.getAuthHeaders()
        });
      }

      deleteHousing(id: number): Observable<any> {
       
        return this.http.delete(`${this.url}/housing/delete/${id}`,{
            headers: this.getAuthHeaders()
          });
       
      }

    getAllSatge() {
        return this.http.get(`${this.url}/housing`);
    }
    getSatgeById(id: any) {
        return this.http.get(`${this.url}/api/stages/${id}`);
    }
    addSatge(data: any) {
        return this.http.post(`${this.url}/api/stages`, data);
    }
    editSatge(data: any) {
        return this.http.put(`${this.url}/api/stages/${data.id}`, data);
    }
    deleteSatge(id: any) {
        return this.http.delete(`${this.url}/api/stages/${id}`);
    }
}
