import { Component } from '@angular/core';
import { HousingService } from './housing.service';
import { Housing } from './housing.model';

@Component({
  selector: 'app-add-housing',
  templateUrl: './add-housing.component.html',
  styleUrls: ['./add-housing.component.scss']
})
export class AddHousingComponent {
  housing: Housing = {
    address: '',
    description: '',
    price: 0,
    availability: true,
    photos: [],
    availableRooms: 0,
    amenities: [],
    characteristics:''
  };

  // Temporary strings to capture comma-separated input
  photosString: string = '';
  amenitiesString: string = '';


  constructor(private housingService: HousingService) {}

  onSubmit() {
    // Convert photosString and amenitiesString to arrays
    if (this.photosString) {
      this.housing.photos = this.photosString.split(',').map(photo => photo.trim());
    }
    if (this.amenitiesString) {
      this.housing.amenities = this.amenitiesString.split(',').map(amenity => amenity.trim());
    }

    // Call the service to submit the housing data
    this.housingService.createHousing(this.housing).subscribe(
      (response) => {
        console.log('Housing added successfully:', response);
      },
      (error) => {
        console.error('Error adding housing:', error);
      }
    );
  }
}
<h2 mat-dialog-title class="dialog-title">Add New Housing</h2>

<mat-dialog-content class="dialog-content">
    <form #housingForm="ngForm" (ngSubmit)="onSubmit()" class="form-container">
        <!-- Address -->
        <div class="form-group">
            <label for="address">Address:</label>
            <input type="text" class="form-control" id="address" name="address" [(ngModel)]="housing.address" required>
        </div>

        <!-- Description -->
        <div class="form-group">
            <label for="description">Description:</label>
            <textarea class="form-control" id="description" name="description" [(ngModel)]="housing.description" required></textarea>
        </div>

        <!-- Price -->
        <div class="form-group">
            <label for="price">Price:</label>
            <input type="number" class="form-control" id="price" name="price" [(ngModel)]="housing.price" required>
        </div>

        <!-- Availability -->
        <div class="form-group">
            <label for="availability">Availability:</label>
            <input type="checkbox" class="form-control" id="availability" name="availability" [(ngModel)]="housing.availability">
        </div>

        <!-- Available Rooms -->
        <div class="form-group">
            <label for="availableRooms">Available Rooms:</label>
            <input type="number" class="form-control" id="availableRooms" name="availableRooms" [(ngModel)]="housing.availableRooms" required>
        </div>

        <!-- Photos -->
        <div class="form-group">
            <label for="photos">Photos (Comma separated URLs):</label>
            <input type="text" class="form-control" id="photos" name="photos" [(ngModel)]="photosString" required>
        </div>

        <!-- Amenities -->
        <div class="form-group">
            <label for="amenities">Amenities (Comma separated):</label>
            <input type="text" class="form-control" id="amenities" name="amenities" [(ngModel)]="amenitiesString" required>
        </div>
        <!-- characteristics -->
        <div class="form-group">
            <label for="characteristics">Characteristics :</label>
            <textarea class="form-control" id="characteristics" name="characteristics" [(ngModel)]="housing.characteristics" required></textarea>
        </div>
    </form>
</mat-dialog-content>

<mat-dialog-actions align="end">
    <button mat-button mat-dialog-close>Close</button>
    <button mat-raised-button [mat-dialog-close]="true" color="primary" (click)="onSubmit()" cdkFocusInitial>Save</button>
</mat-dialog-actions>
