import { Component, OnInit } from '@angular/core';
import { LogementService } from '../services/logement.service';

@Component({
  selector: 'app-logement',
  templateUrl: './logement.component.html',
  styleUrls: ['./logement.component.scss'],
})
export class LogementComponent implements OnInit {
  logements: any[] = [];
  personnel: any[] = [];
  messages: any[] = [];
  newLogement: { name: string; address: string } = { name: '', address: '' };
  logementIdToAddUser!: number;
  userEmailToAdd!: string;
  messageToSend!: string;
  errorMessage: string = '';
  successMessage: string = '';
  loadingMessages: boolean = false;

  newMessage: string = '';




  constructor(private logementService: LogementService) {}

  ngOnInit(): void {
    this.getLogementPersonnel();
    this.getAllLogements();  
    this.getLogementMessages();

  }


  getLogementPersonnel(): void {
    this.logementService.getLogementPersonnelForCurrentUser().subscribe(
      (data) => {
        this.personnel = data.userPersonnels; 
      },
      (error) => {
        this.errorMessage = 'Error loading logement personnel.';
        this.setTimeoutToClearMessages();
      }
    );
  }






  // Fetch all logements
  getAllLogements(): void {
    this.logementService.getAllLogements().subscribe(
      (data) => {
        this.logements = data;
      //  this.getMyLogementMessages(); // Fetch messages for all logements
      },
      (error) => {
        this.errorMessage = 'Error loading logements.';
      }
    );
  }



  // Fetch messages related to the current logement
  getMyLogementMessages(): void {
    this.logementService.getLogementMessages().subscribe(
      (data) => {
        this.messages = data;
      },
      (error) => {
        this.errorMessage = 'Error retrieving messages.';
        this.setTimeoutToClearMessages();
      }
    );
  }

  // Delete a logement
  deleteLogement(logementId: number): void {
    this.logementService.deleteLogement(logementId).subscribe(
      () => {
        this.getAllLogements();  // Refresh the logements list after deletion
        this.successMessage = 'Logement deleted successfully!';
        this.setTimeoutToClearMessages();
      },
      (error) => {
        this.errorMessage = 'Error deleting logement.';
        this.setTimeoutToClearMessages();
      }
    );
  }






  // Create a new logement
  createLogement(): void {
    const { name, address } = this.newLogement;
    this.logementService.createLogement(name, address).subscribe(
      (response) => {
        this.getAllLogements();  // Refresh logements after creating new one
        this.newLogement = { name: '', address: '' };  // Reset form
        this.successMessage = 'Logement created successfully!';
        this.setTimeoutToClearMessages();
      },
      (error) => {
        this.errorMessage = 'Error creating logement.';
        this.setTimeoutToClearMessages();
      }
    );
  }

  addUserToLogement(): void {
    if (!this.userEmailToAdd) {
      this.errorMessage = 'Please provide a valid email.';
      this.setTimeoutToClearMessages();
      return;
    }

    this.logementService.addUserToLogement(this.userEmailToAdd).subscribe(
      (response) => {
        this.successMessage = 'User added successfully!';
        this.userEmailToAdd = ''; 
        this.setTimeoutToClearMessages();
        this.getLogementPersonnel(); // Rnel dataefresh person
      },
      (error) => {
        this.errorMessage = 'Error adding user to logement.';
        this.setTimeoutToClearMessages();
      }
    );
  }




  deleteUserFromLogement( userId: number): void {
    this.logementService.deleteUserFromLogement( userId).subscribe(
      () => {
        this.successMessage = 'User removed successfully!';
        this.getLogementPersonnel(); // Refresh personnel list
        this.setTimeoutToClearMessages();
      },
      (error) => {
        this.errorMessage = 'Error removing user from logement.';
        this.setTimeoutToClearMessages();
      }
    );
  }


  // Send a message to the logement








  getLogementMessages(): void {
    this.logementService.getLogementMessages().subscribe(
      (data) => {
        this.messages = data;
      },
      (error) => {
        this.errorMessage = 'Error loading messages.';
        this.setTimeoutToClearMessages();
      }
    );
  }

  sendMessage(): void {
    if (!this.newMessage.trim()) {
      this.errorMessage = 'Message cannot be empty!';
      this.setTimeoutToClearMessages();
      return;
    }

    this.logementService.sendMessageToLogement(this.newMessage).subscribe(
      () => {
        // Clear the input field
        this.newMessage = '';
        // Set a success message
        this.successMessage = 'Message sent successfully!';
        this.setTimeoutToClearMessages();
        // Fetch the updated list of messages
        this.getLogementMessages();
      },
      (error) => {
        // Handle error and display an error message
        this.errorMessage = 'Error sending message. Please try again.';
        console.error('Send message error:', error);
        this.setTimeoutToClearMessages();
      }
    );
  }













  // Helper function to clear messages after a few seconds
  setTimeoutToClearMessages(): void {
    setTimeout(() => {
      this.successMessage = '';
      this.errorMessage = '';
    }, 5000);  // Hide messages after 5 seconds
  }
}
<div class="container" id="logement" style="margin-top: 100px;">
  <!-- Success/Error Alerts -->
  <div *ngIf="successMessage" class="alert alert-success fade-in">
    {{ successMessage }}
    <button type="button" class="btn-close" aria-label="Close" (click)="successMessage = ''"></button>
  </div>
  <div *ngIf="errorMessage" class="alert alert-danger fade-in">
    {{ errorMessage }}
    <button type="button" class="btn-close" aria-label="Close" (click)="errorMessage = ''"></button>
  </div>

  <!-- Create Logement Section -->
  <div class="card mb-4">
    <div class="card-header bg-primary text-white">
      <h3>Create a New Logement</h3>
    </div>
    <div class="card-body">
      <form (submit)="createLogement()" class="form-inline">
        <div class="mb-3">
          <label for="logementName" class="form-label">Name:</label>
          <input
            id="logementName"
            type="text"
            class="form-control"
            [(ngModel)]="newLogement.name"
            name="name"
            placeholder="Enter logement name"
            required
          />
        </div>
        <div class="mb-3">
          <label for="logementAddress" class="form-label">Address:</label>
          <input
            id="logementAddress"
            type="text"
            class="form-control"
            [(ngModel)]="newLogement.address"
            name="address"
            placeholder="Enter address"
            required
          />
        </div>
        <button type="submit" class="btn btn-success">Create Logement</button>
      </form>
    </div>
  </div>

  <!-- List of Logements -->
  <div class="card mb-4">
    <div class="card-header bg-info text-white">
      <h3>Logements</h3>
    </div>
    <div class="card-body">
      <ul class="list-group">
        <li
          *ngFor="let logement of logements"
          class="list-group-item d-flex justify-content-between align-items-center"
        >
          <div>
            <i class="fas fa-home me-2"></i>
            <strong>{{ logement.name }}</strong> - {{ logement.address }}
          </div>
          <div>
            <button
              class="btn btn-danger btn-sm me-2"
              (click)="deleteLogement(logement.id)"
            >
              Delete
            </button>
          </div>
        </li>
      </ul>
    </div>
  </div>

  <!-- Logement Personnel Section -->
  <div class="card mb-4">
    <div class="card-header bg-warning text-white">
      <h3>Logement Personnel</h3>
    </div>
    <div class="card-body">
      <ul class="list-group">
        <li *ngFor="let user of personnel" class="list-group-item d-flex justify-content-between">
          <div>
            <i class="fas fa-user me-2"></i>
            <strong>{{ user.firstName }} {{ user.lastName }}</strong> - {{ user.email }}
            <p class="mb-0">Phone: {{ user.phoneNumber }}</p>
          </div>
          <button
            class="btn btn-danger btn-sm"
            (click)="deleteUserFromLogement(user.id)"
          >
            Remove
          </button>
        </li>
      </ul>

      <form (submit)="addUserToLogement()" class="form-inline">
        <div class="mb-3">
          <label for="userEmail" class="form-label">User Email:</label>
          <input
            id="userEmail"
            type="email"
            class="form-control"
            [(ngModel)]="userEmailToAdd"
            name="userEmail"
            placeholder="Enter user email"
            required
          />
        </div>
        <button type="submit" class="btn btn-primary">Add User</button>
      </form>
    </div>
  </div>

  <!-- Chat Messages Section -->
  <div class="card mb-4" id="chat">
    <div class="card-header bg-dark text-white">
      <h3>Chat Messages</h3>
    </div>
    <div class="card-body d-flex flex-column justify-content-between">
      <!-- Loading Spinner -->
      <div *ngIf="loadingMessages" class="text-center my-3">
        <div class="spinner-border text-primary" role="status">
          <span class="visually-hidden">Loading...</span>
        </div>
      </div>

      <!-- Messages -->
      <ul class="list-group flex-grow-1" *ngIf="!loadingMessages && messages.length > 0">
        <li *ngFor="let message of messages" class="list-group-item">
          <div class="d-flex justify-content-between">
            <div>
              <i class="fas fa-comment me-2"></i>
              <strong>{{ message.sendFrom?.firstName || 'Unknown' }}</strong>: 
              {{ message.message || 'No message content' }}
            </div>
            <span class="text-muted small">{{ message.date | date: 'short' }}</span>
          </div>
        </li>
      </ul>
      <div *ngIf="!loadingMessages && messages.length === 0" class="text-muted text-center">
        No messages to display.
      </div>

      <!-- Message Input -->
      <form (submit)="sendMessage()" class="mt-3">
        <div class="input-group">
          <input
            type="text"
            class="form-control"
            [(ngModel)]="newMessage"
            name="newMessage"
            placeholder="Type your message"
            required
          />
          <button class="btn btn-primary" type="submit">Send</button>
        </div>
      </form>
    </div>
  </div>
</div>
